create table system_emails (
    email_id		tinyint unsigned not null auto_increment,
    from_addr		varchar(255) not null,
    subject		varchar(255) not null default 'Resume Received',
    body		text not null,
    url			varchar(255) not null,
    descr		text not null,

    primary key		(email_id)
) comment='System-generated messages';
insert into system_emails (body,descr) values ('%firstname%,

Thank you for applying for job number %jobnumber%.
We have successfully received your application and we will contact you
shortly','This is the email that will be sent to users when they fill out your
job application. You have the choice of sending a text or html
email. When you fill out the form below you must always fill out the
text version in case the client does not have a html email program.');


insert into pages (name,description,mode,navgroup,buttons_no,button1,button2)
values ('/users/jobs.php','After applied for a job','job2',3,3,'Header Banner',
    'Footer Banner');
