create table email_stats (
    stats_id		mediumint unsigned not null auto_increment,
    email_id		mediumint unsigned not null,
    list		text not null,
    rejected		text not null,
    transient_errors	text not null,
    sent		datetime not null,
    uniq		varchar(16) not null,

    primary key		(stats_id)
) comment='Statistics about email campaigns mailout';

alter table email_campaigns
    add from_addr	varchar(255) not null after subject,
    add reply_to	varchar(255) not null after subject,
    add return_path	varchar(255) not null after reply_to,
    add attach1_name	varchar(255) not null,
    add attach1		mediumblob not null,
    add attach2_name	varchar(255) not null,
    add attach2		mediumblob not null,
    add cron_d		date not null,
    add cron_t		time not null,
    add cron_lock	tinyint unsigned not null default 0,
    add use_uniq	tinyint unsigned not null default 1,
    add uniqid		varchar(32) not null;

create table documents_categories (
    document_id		mediumint unsigned not null,
    category_id		tinyint unsigned not null,

    primary key		(document_id,category_id)
) comment='Multiple categories per document';

insert into documents_categories (document_id,category_id)
select document_id,category_id from documents;

alter table documents
    drop category_id;

alter table config
    add bounce_hard		varchar(32) not null default 'bounce_ignore',
    add hard_notify		tinyint unsigned not null default 1 after bounce_hard,
    add hard_move		mediumint unsigned not null default 0 after hard_notify,
    add bounce_soft		varchar(32) not null default 'bounce_ignore' after hard_move,
    add soft_notify		tinyint unsigned not null default 1 after bounce_soft,
    add soft_move		mediumint unsigned not null default 0 after soft_notify,
    add bounce_success	varchar(32) not null default 'bounce_ignore' after soft_move,
    add success_notify	tinyint unsigned not null default 1 after bounce_success,
    add success_move	mediumint unsigned not null default 0 after success_notify,
    add pop_server	varchar(255) not null,
    add pop_user	varchar(255) not null,
    add pop_password	varchar(255) not null,
    add pop_port	varchar(255) not null default '110',
    add pop_delmail	tinyint unsigned not null default 1;

alter table job_config
    add apply_success		varchar(255) not null default 'You have successfully applied for job:',
    add apply_reject		varchar(255) not null default 'You already applied for this job.';
